import { useState } from "react";
import "./pomodoro.css";
// import React, { useState, useEffect } from "react";

function Pomodoro() {
  let [stopwatch, setStopwatch] = useState(99);
  let isCounterActive = false;

  const handleClick = () => {
    isCounterActive = true;
    setInterval(() => {
      setStopwatch((prevCount) => prevCount - 1);
    }, 1000);
  };
  return (
    <div className="Pomodoro">
      <div className="pomodoroCounter">{stopwatch}</div>
      {isCounterActive ? "sss" : "bobo"}
      {/* <button className="pomodoroButton" onClick={handleClick}> */}
      {/* START */}
      {/* </button> */}
    </div>
  );
}

export default Pomodoro;
