import "./todo.css";
import React, { useState, useEffect } from "react";

function Todo() {
  let [todos, setTodos] = useState([]);
  console.log(todos);

  function pushTodo() {
    setTodos([...todos, document.getElementById("todoTextData").value]); //simple value
  }

  function deleteTodo() {
    setTodos((todo2) => todo2.filter((_, index) => index !== 0));
  }

  let TodoElements = () => {
    if (todos.length === 0) {
      return (
        <div className="todoNote">
          <p>PUSTO!!!</p>
        </div>
      );
    } else {
      let listTodos = todos.map((todo) => (
        <div style={{ display: "flex" }} key={todo}>
          <div className="todoNote">{todo}</div>
          <button
            onClick={deleteTodo}
            className="todoButton"
            style={{ marginTop: "1%", marginBottom: "1%" }}
          >
            ‚ùå
          </button>
        </div>
        // <li key={todo}>{todo}</li>
      ));
      return <> {listTodos} </>;
    }
  };

  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button onClick={pushTodo} className="todoButton">
          üí¨
        </button>
      </div>
      <div className="todos" id="todoList">
        {/* {todo} */}
        <TodoElements />
      </div>
    </div>
  );
}

export default Todo;
