import "./todo.css";
import * as ReactDOM from "react-dom";
import React, { useState, useEffect } from "react";
let todos = [];
// if (window.localStorage.getItem("todos" == null)) {
// console.log("nie ma w localu");
// } else {
// todos = JSON.parse(window.localStorage.getItem("todos"));
// console.log(todos);
// }

let pushTodo = () => {
  useEffect(() => {
    console.log("DDDD");
  });
  todos.push(document.getElementById("todoTextData").value);
  console.log(todos);
  ReactDOM.render(<TodoElements />, document.getElementById("todoList"));
  // localStorage.setItem("todos", JSON.stringify(todos));
};
let TodoElements = () => {
  if (todos.length === 0) {
    return <div className="todoNote">PUSTO!!!</div>;
  } else {
    let listTodos = todos.map((todos) => (
      <div className="todoNote">{todos}</div>
    ));
    return <> {listTodos} </>;
  }
};
function Todo() {
  useEffect(() => {
    console.log("DDDD");
  });
  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button onClick={pushTodo} className="todoButton">
          ðŸ’¬
        </button>
      </div>
      <div className="todos" id="todoList">
        <TodoElements />
      </div>
    </div>
  );
}

export default Todo;
