import { useState } from "react";
import "./pomodoro.css";
// import React, { useState, useEffect } from "react";

function Pomodoro() {
  let timer;
  let [stopwatch, setStopwatch] = useState(20);

  const StopwatchStart = () => {
    timer =
      !timer &&
      setInterval(() => {
        setStopwatch((prevCount) => prevCount + 1);
      }, 1000);

    if (stopwatch === 3) clearInterval(timer);
  };
  return (
    <div className="Pomodoro">
      <div className="pomodoroCounter">
        <StopwatchStart />
      </div>
      <button className="pomodoroButton" onClick={StopwatchStart}>
        START
      </button>
    </div>
  );
}

export default Pomodoro;
