import "./todo.css";
let todos = [];

let pushTodo = () => {
  todos.push(document.getElementById("todoTextData").value);
  console.log(todos);
  const TodoElements = () => {
    const listTodos = todos.map((todos) => <li>{todos}</li>);
    return <div className="todoNote">{listTodos}</div>;
  };
};
function Todo() {
  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button onClick={pushTodo}>ðŸ’¬</button>
      </div>
      <div className="todos">
        <TodoElements />
      </div>
    </div>
  );
}

export default Todo;
