import "./styles.css";
import React, { useState, useEffect } from "react";

function Todo({ todos }, { formTodo }, { setTodos }, setFormTodo) {
  // function handleTodoSubmit() {}
  console.log(setTodos);

  function pushTodo() {
    if (formTodo === "") {
      alert("Task Must be Named");
    } else {
      setTodos([
        ...todos,
        {
          content: formTodo,
          id: new Date(),
          isFinished: false,
        },
      ]);
      setFormTodo("");
      document.getElementById("todoTextData").value = "";
    }

    //simple value
  }

  function removeTodo(id) {
    const newList = todos.filter((item) => item.id !== id);
    setTodos(newList);
  }

  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input
          type="text"
          id="todoTextData"
          className="todoText"
          onChange={(e) => setFormTodo(e.target.value)}
        />
        <button className="todoButton" onClick={pushTodo}>
          üí¨
        </button>
      </div>
      <div className="todos" id="todoList">
        {todos.map((item) => (
          <div style={{ display: "flex" }} key={todos.id}>
            <div className="box">{item.content}</div>
            <button
              onClick={() => removeTodo(item.id)}
              className="todoButton"
              style={{ marginTop: "1%", marginBottom: "1%" }}
            >
              ‚ùå
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Todo;
