import "./styles.css";
import React, { useState, useEffect } from "react";

function pushTodo() {
  setTodos([
    ...todos,
    {
      content: document.getElementById("todoTextData").value,
      id: new Date(),
    },
  ]);
  document.getElementById("todoTextData").value = "";
}

function removeTodo(id) {
  const newList = todos.filter((item) => item.id !== id);
  setTodos(newList);
}

function Todo() {
  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button className="todoButton" onClick={pushTodo}>
          üí¨
        </button>
      </div>
      <div className="todos" id="todoList">
        {todos.map((item) => (
          <div style={{ display: "flex" }} key={todos.id}>
            <div className="box">{item.content}</div>
            <button
              onClick={() => removeTodo(item.id)}
              className="todoButton"
              style={{ marginTop: "1%", marginBottom: "1%" }}
            >
              ‚úÖ
            </button>
            <button
              onClick={() => removeTodo(item.id)}
              className="todoButton"
              style={{ marginTop: "1%", marginBottom: "1%" }}
            >
              ‚ùå
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Todo;
