import { useState } from "react";
import "./pomodoro.css";
// import React, { useState, useEffect } from "react";

function Pomodoro() {
  let [stopwatch, setStopwatch] = useState(10000);

  function StopwatchStart() {
    setInterval(() => {
      setStopwatch((prevCount) => prevCount + 1);
    }, 1000);

    if (stopwatch <= 0) {
      alert("koniec");
    }
    return <div>{stopwatch}</div>;
  }
  return (
    <div className="Pomodoro">
      <div className="pomodoroCounter">{/* <StopwatchStart /> */}</div>
      <button
        className="pomodoroButton"
        // onClick={() => setStopwatch(stopwatch - 1)}
      >
        START
      </button>
    </div>
  );
}

export default Pomodoro;
