    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button onClick={pushTodo}>ðŸ’¬</button>
      </div>
      <div className="todos">
        <TodoElements />
      </div>
    </div>
  );
}

export default Todo;
