// ));
// return <> {listTodos} </>;
// }
// };
function Todo() {
  let [todo, setTodo] = useState([]);
  console.log(todo);

  function pushTodo() {
    setTodo([...todo, document.getElementById("todoTextData").value]); //simple value
  }

  function deleteTodo() {}
  let TodoElements = () => {
    if (todo.length === 0) {
      return (
        <div className="todoNote">
          <p>PUSTO!!!</p>
        </div>
      );
    } else {
      let listTodos = todo.map((todos) => (
        <div style={{ display: "flex" }} key={todo}>
          <div className="todoNote">{todos}</div>
          <button
            onClick={pushTodo}
            className="todoButton"
            style={{ marginTop: "1%", marginBottom: "1%" }}
          >
            ‚ùå
          </button>
        </div>
      ));
      return <> {listTodos} </>;
    }
  };

  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button onClick={pushTodo} className="todoButton">
          üí¨
        </button>
      </div>
      <div className="todos" id="todoList">
        {/* {todo} */}
        <TodoElements />
      </div>
    </div>
  );
}

export default Todo;
