import "./styles.css";
import TodoElement from "./TodoElement";
import React, { useState, useEffect } from "react";

function Todo() {
  let [todos, setTodos] = useState(["spotkanie na tappczanie"]);

  // function handleTodoSubmit() {}

  const allTodos = todos.map((newTodo) => (
    <TodoElement content={newTodo} key={new Date()} />
  ));

  function pushTodo() {
    setTodos([...todos, document.getElementById("todoTextData").value]); //simple value
  }

  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button className="todoButton" onClick={pushTodo}>
          ðŸ’¬
        </button>
      </div>
      <div className="todos" id="todoList">
        {/* <TodoElement content="pipka" /> */}
        {allTodos}
      </div>
    </div>
  );
}

export default Todo;
