import "./todo.css";
import * as ReactDOM from "react-dom";
let todos = ["dwdwdw", "dwdwdw", "dwdwd"];

let pushTodo = () => {
  todos.push(document.getElementById("todoTextData").value);
  console.log(todos);
  ReactDOM.render(<TodoElements />);
};
let TodoElements = () => {
  let listTodos = todos.map((todos) => <li>{todos}</li>);
  return <div className="todoNote">{listTodos}</div>;
};
function Todo() {
  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button onClick={pushTodo}>ðŸ’¬</button>
      </div>
      <div className="todos">
        <TodoElements />
      </div>
    </div>
  );
}

export default Todo;
