import React, { useState, useEffect } from "react";

function Todo() {
  let [todos, setTodos] = useState(["spotkanie na tappczanie"]);

  // function handleTodoSubmit() {}

  function pushTodo() {
    setTodos([...todos, document.getElementById("todoTextData").value]); //simple value
  }

  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button className="todoButton" onClick={pushTodo}>
          üí¨
        </button>
      </div>
      <div className="todos" id="todoList">
        <div style={{ display: "flex" }}>
          <div className="todoNote">{props.content}</div>
          <button
            onClick={console.log("deleted")}
            className="todoButton"
            style={{ marginTop: "1%", marginBottom: "1%" }}
          >
            ‚ùå
          </button>
        </div>
      </div>
    </div>
  );
}

export default Todo;
