import "./todo.css";
import * as ReactDOM from "react-dom";
let todos = [];
// todos = localStorage.getItem("todos");
// console.log(todos);

let pushTodo = () => {
  todos.push(document.getElementById("todoTextData").value);
  console.log(todos);
  ReactDOM.render(<TodoElements />, document.getElementById("todoList"));
  localStorage.setItem("todos", todos);
};
let TodoElements = () => {
  let listTodos = todos.map((todos) => <div className="todoNote">{todos}</div>);
  return <> {listTodos} </>;
};
function Todo() {
  return (
    <div className="Todo">
      <div style={{ display: "flex" }}>
        <input type="text" id="todoTextData" className="todoText" />
        <button onClick={pushTodo} className="todoButton">
          ðŸ’¬
        </button>
      </div>
      <div className="todos" id="todoList">
        <TodoElements />
      </div>
    </div>
  );
}

export default Todo;
